            // 旋转到中心
            var cityCoord = city.coord;
            var rotateRad = rotate2Center(cityCoord);
            var finalY = - rotateRad.y;
            while(earthGroup.rotation.y>0 && finalY+Math.PI*2 < earthGroup.rotation.y) finalY += Math.PI*2;
            while(earthGroup.rotation.y<0 && finalY-Math.PI*2 > earthGroup.rotation.y) finalY -= Math.PI*2;
